import React from "react";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition'
const SpeechRecog =()=>{
    const {transcript,resetTranscript} = useSpeechRecognition()
   const aka=()=>{
    SpeechRecognition.preventDefault();
    SpeechRecognition.startListening({continuous:true})
    document.getElementById("srr").setAttribute('transcript', 'Default value')
   }
    return (
        <div>
            <form>
            <textarea value={transcript} class="srr"> </textarea>
                <button onClick={aka}>Start Listening</button>
                
                <button onClick={resetTranscript}>Clear</button>
                <button onClick={(e) =>{e.preventDefault(); SpeechRecognition.stopListening({continuous:true})}}>Stop Listening</button>
            </form>
        </div>
    )
}
export default SpeechRecog
