import {useState} from 'react';
import React from 'react'
import './drop.css';
export default function Drop() {
        const [file, setFile] =useState(null);
        const onInputChange=(e)=>{
          setFile(e.target.value)
        };
      
  return <form method="post" action="#" id="#">
           
              
              
              
  <div class="form-group files"><br></br><br></br><center>    <label class="aa">Upload Your File </label><br></br><br></br>
    <div class="abc"><input type="file" onChange={onInputChange} class="form-control" multiple=""/></div></center>

  </div>
  

</form>
}
