import React from "react";
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition'
const SpeechRecog =()=>{
    if (!browserSupportsSpeechRecognition) {
        console.log('NOOOOOOOO')
      }
    const {transcript,resetTranscript,browserSupportsSpeechRecognition} = useSpeechRecognition()
   
   let aka=()=>{
   
    SpeechRecognition.startListening({continuous:true})
    document.getElementById("srr").value=transcript
    
   }
   let closesr=()=>{
   SpeechRecognition.stopListening({continuous:true})
    
   }
    return (
        <div>
            <form>
            <textarea value={transcript} id="srr"> </textarea>
                <button onClick={aka}>Start Listening</button>
                
                <button onClick={resetTranscript}>Clear</button>
                <button onClick={closesr}>Stop Listening</button>
            </form>
        </div>
    )
}
export default SpeechRecog
