import React,{useState} from 'react';

const options=[
  {key:'en', text:'English' , value:'male'}
]
export default function trans() {
  
  const [inputText,setInputText]=useState('')
  const [resultText,setResultText]=useState('')
  const translateText =()=>{
    setResultText(inputText)
  }
  return (
    
    <div>
    <div class="a">
      Translator
    </div>

<form>
  
    <div class="form-group">
    <label for="exampleFormControlTextarea1">Enter text</label>
    <textarea class="form-control" id="exampleFormControlTextarea1" rows="1" onChange={(e) => setInputText(e.target.value)}></textarea>
  </div>
  <br></br> <br></br> <br></br> <br></br>

  <div class="dropdown">
  <button class="btn btn-primary dropdown-toggle" type="button" data-toggle="dropdown">Dropdown Example
  <span class="caret"></span></button>
  options={options}
</div><br></br><br></br><br></br><br></br><br></br>


<div class="form-group">
    <label for="exampleFormControlTextarea1">Result</label>
    <textarea class="form-control" id="exampleFormControlTextarea1" rows="1" value={resultText}></textarea>
  </div>

  <div className="form-group">
    <button type="button"className="btn btn-outline-dark" onClick={translateText}>Translate</button>
    </div>


</form>
    
  







    </div>
    
  );
}
