import React from "react";
import Tesseract from "tesseract.js";
import './drop.css';
import jsPDF from 'jspdf';


export default function Drop() {  
   
    const [image, setImage] = React.useState('');  
    const handleClick=()=>{
        
        Tesseract.recognize(
            image,
            'eng',
            { logger: m => {console.log(m);} }
          ).then(({ data: { text } }) => {
           
          });
        }
       
      const generatepdf=()=>{
          var doc=new jsPDF('landscape','px','a4','false');
          doc.text(60,410,'Hello')
          doc.save('a.pdf')
      }



  return <form method="post" action="#" id="#">   
  <div className="form-group files"><br></br><br></br><center>    <label className="aa">Upload Your File </label><br></br><br></br>
    <div className="abc"><input type="file" 
     className="form-control"
     onChange={(e)=>setImage(URL.createObjectURL(e.target.files[0]))}
     multiple=""/></div></center>
    <div className="sub"><button 
    type="button" 
    className="btn btn-outline-dark"
    onClick={handleClick}
    >Submit</button></div>

    <div className="dow"><button 
    type="button" 
    className="btn btn-outline-dark"
    onClick={generatepdf}
    >Download</button></div>
    
  </div>
  

</form>
}
