import React from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

export default function SpeechRecog ()  {
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();
  const onSiteChanged=()=> {
    SpeechRecognition.startListening({ language: 'hi-IN'});
  }
  const onSiteChanged1=()=> {
    SpeechRecognition.startListening({ language: 'kn-IN'});
  }
  const onSiteChanged2=()=> {
    SpeechRecognition.startListening({ language: 'en-US'});
  }
  if (!browserSupportsSpeechRecognition) {
    return <span>This Browser doesn't support speech recognition.</span>;
  }
  let sr={
    position: "absolute",
    fontSize: "35px",
    left:"635px",
    top: "1060px"
  }
  let mp={
    position: "absolute",
    fontSize: "22px",
    left:"650px",
    top: "1140px"
  }

  return (
    <div>
      <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>
      <div style={sr}>Speech Recognition</div>
      <div style={mp}>Microphone: {listening ? 'on' : 'off'}</div>
      

      
      
      <div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={onSiteChanged} />
  <label class="form-check-label" for="flexRadioDefault1">
    Hindi
  </label>
  
</div>
<div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2"onChange={onSiteChanged1} />
  <label class="form-check-label" for="flexRadioDefault2">
    Kannada
  </label>
</div>
<div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2"onChange={onSiteChanged2} />
  <label class="form-check-label" for="flexRadioDefault2">
    English
  </label>
</div>



<button className="btn btn-outline-dark" onClick={(e)=>{e.preventDefault();
  SpeechRecognition.startListening({ continuous: true})

  }}>Start for more time</button>

      <button className="btn btn-outline-dark" onClick={(e)=>{e.preventDefault();SpeechRecognition.stopListening({ continuous: true })}}>Stop</button>
      <button className="btn btn-outline-dark" onClick={resetTranscript}>Reset</button>
      <textarea class="form-control" value={transcript}></textarea>
    </div>
  );
}
