import React,{useState} from 'react';
import Tesseract from 'tesseract.js';
import './drop.css';
export default function Drop() {
    const[image,setImage]=useState(0);
    const handleClick=()=>{
        setIsLoading(true);
        Tesseract.recognize(
            image,
            'eng',
            { logger: m => {console.log(m);} }
          ).then(({ data: { text } }) => {
            setText(text);
            setIsLoading(false);
          });
    }    
      
  return <form method="post" action="#" id="#">
           
              
              
              
  <div class="form-group files"><br></br><br></br><center>    <label class="aa">Upload Your File </label><br></br><br></br>
    <div class="abc"><input type="file"  class="form-control" multiple=""/></div></center>
    <div class="sub"><button 
    type="button" 
    class="btn btn-outline-dark"
    onclick={handleClick}
    >Submit</button></div>

  </div>
  

</form>
}
