import React from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

const SpeechRecog = () => {
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return <span>This Browser doesn't support speech recognition.</span>;
  }

  return (
    <div>
      <p>Microphone: {listening ? 'on' : 'off'}</p>
      

      
      
      <div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" />
  <label class="form-check-label" for="flexRadioDefault1">
    Hindi
  </label>
  
</div>
<div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" />
  <label class="form-check-label" for="flexRadioDefault2">
    Kannada
  </label>
</div>


<button className="btn btn-outline-dark" onClick={(e)=>{e.preventDefault();
  SpeechRecognition.startListening({ continuous: true})
  if(document.getElementById("flexRadioDefault1").checked = true){SpeechRecognition.startListening({ language: 'hi-IN'})}
  }}>Start</button>

      <button className="btn btn-outline-dark" onClick={(e)=>{e.preventDefault();SpeechRecognition.stopListening({ continuous: true })}}>Stop</button>
      <button className="btn btn-outline-dark" onClick={resetTranscript}>Reset</button>
      <textarea class="form-control" value={transcript}></textarea>
    </div>
  );
};  
export default SpeechRecog
